import styled from "styled-components";
import CocoFiber from "../images/serabut.webp";

const CardStyled = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem;
  background-color: salmon;

  div.img-box {
    cursor: pointer;
    img {
      width: 100%;
      height: auto;
    }
  }
`;

const ProductCard = () => {
  return (
    <CardStyled>
      <a href="">
        <div className="img-box">
          <img src={CocoFiber} alt="Coconut Fiber" />
        </div>
      </a>
      <div className="text-box">
        <h3>Coconut Fiber</h3>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Laudantium
          earum iste consequatur?
        </p>
      </div>
    </CardStyled>
  );
};

export default ProductCard;
