import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { FaBars } from "react-icons/fa";

const NavbarStyled = styled.nav`
  position: relative;
  background: #000;
  height: 60px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem calc((100vw - 1000px) / 2);
  z-index: 10;
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;

  @media screen and (max-width: 768px) {
    display: ${(props) => (props.isOpen ? "flex" : "none")};
    position: absolute;
    flex-direction: column;

    width: 100%;
    height: 100vh;
    background-color: salmon;
    z-index: 20;
  }
`;

const NavlinkStyled = styled(NavLink)`
  text-decoration: none;
  color: #fff;
  display: flex;
  align-items: center;
  padding: 0 1rem 0.3rem;
  height: 100%;
  cursor: pointer;
  border-bottom: 2px solid rgba(0, 0, 0, 0);
  &:hover:not(.navbar-brand) {
    border-color: #15cdfc;
  }
  &.active:not(.navbar-brand) {
    color: #15cdfc;
  }
  &.navbar-brand {
    font-size: 1.5rem;
  }
`;
const Bars = styled(FaBars)`
  display: none;
  color: #fff;
  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 75%);
    font-size: 1.8rem;
    cursor: pointer;
    z-index: 21;
  }
`;

const Navbar = () => {
  const [burgerOpen, setBurgerOpen] = useState(false);
  return (
    <NavbarStyled>
      <NavlinkStyled to="/" className="navbar-brand">
        Universal Coco
      </NavlinkStyled>
      <Bars isOpen={burgerOpen} onClick={() => setBurgerOpen(!burgerOpen)} />
      <NavMenu isOpen={burgerOpen}>
        <NavlinkStyled to="/about">About</NavlinkStyled>
        <NavlinkStyled to="/contact">Contact Us</NavlinkStyled>
      </NavMenu>
    </NavbarStyled>
  );
};

export default Navbar;
