import React from "react";
import styled from "styled-components";
import illustration from "../../images/about-us.webp";
const AboutStyled = styled.div`
  padding: 2rem 3rem;
  display: flex;
  align-items: center;
  gap: 30px;
  div.img-box {
    flex: 2;
    img {
      width: 100%;
      height: auto;
    }
  }
  div.text-box {
    flex: 3;
    h1 {
      font-size: 3.188rem;
    }
    p {
      font-size: 1.938rem;
      font-family: "Playfair Display", serif;
    }
  }
`;
const AboutSection = () => {
  return (
    <AboutStyled>
      <div className="img-box">
        <img src={illustration} alt="illustration" />
      </div>
      <div className="text-box">
        <h1>About Us</h1>
        <p>
          is an Indonesian company engaged in the export of coconut derivative
          products,supplying processed coconut products. We provide the best and
          high quality coconut products from Indonesia for the whole world.
        </p>
        <a href="">read more</a>
      </div>
    </AboutStyled>
  );
};

export default AboutSection;
