import React from "react";
import styled from "styled-components";
import logo from "../images/logo-uai.webp";
import { MdEmail, MdLocationPin } from "react-icons/md";
import { AiFillPhone } from "react-icons/ai";
import GoogleMap from "./GoogleMap";
import Form from "./Contact/Form";

const FooterStyled = styled.div`
  background-color: #2e2f32;
  color: #fff;
  div.contact {
    display: flex;
    padding: 1rem 2rem;
    justify-content: center;
    div.form {
      flex: 1;
    }
    div.contact-data {
      flex: 1;
      padding: 1rem;
      padding-top: 2rem;
      display: flex;
      flex-direction: column;
      justify-content: start;
      p {
        padding: 1rem 0;
        font-size: 1.6rem;
        line-height: 1.6;
      }
      div.logo-box {
        width: 100%;
      }
      h1 {
        font-family: "Inter", sans-serif;
      }
    }
  }
  div.map-container {
    padding: 3rem 3rem 1rem;
  }

  div.copyright {
    background-color: black;
    font-size: 0.8rem;
    padding: 0.5rem 0;
    text-align: center;
  }

  @media (max-width: 800px) {
    div.map-container {
      padding: 2rem 1.5rem;
    }
    div.contact {
      flex-direction: column;
      div.contact-data {
        div.logo-box {
          width: 40%;
        }
      }
    }
  }

  @media (max-width: 550px) {
    div.contact {
      flex-direction: column;
      padding: 0;
      div.contact-data {
        p {
          font-size: 1.3rem;
          padding: 0.5rem 0;
        }
        h1 {
          font-size: 1.3rem;
        }
      }
    }
  }
`;

const FooterForm = () => {
  return (
    <FooterStyled>
      <div className="map-container">
        <GoogleMap />
      </div>
      <div className="contact">
        <div className="form">
          <Form />
        </div>
        <div className="contact-data">
          <p>
            <AiFillPhone /> +62 811 9444 283 (Benedictus Adriel)
          </p>
          <p>
            <MdEmail /> universalagrobisnis@gmail.com
          </p>
          <p>
            <MdLocationPin />
            Jl. Raya Abdullah Safe'i No.1 / Tebet Utara Dalam No.34, RT.5/RW.1,
            Tebet Tim., Tebet, Kota Jakarta Selatan, Daerah Khusus Ibukota
            Jakarta 12820
          </p>
          <div className="logo">
            <div className="logo-box">
              <img src={logo} alt="UAI logo" />
            </div>
            <h1>PT Universal Agrobisnis Indonesia</h1>
          </div>
        </div>
      </div>

      <div className="copyright">
        <p>Copyright Universal Agrobisnis Indonesia 2021</p>
      </div>
    </FooterStyled>
  );
};

export default FooterForm;
