import React from "react";
import styled from "styled-components";
import CocoFiber from "../images/serabut.webp";

const CardStyled = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-evenly;
  gap: 2rem;
  padding: 1rem;
  border: 3px solid #000;
  border-radius: 12px;
  a {
    text-decoration: none;
    color: inherit;
  }
  div.img-box {
    cursor: pointer;
    img {
      width: 100%;
      height: auto;
    }
  }
  div.text-box {
    text-align: center;
    h3 {
      font-size: 1.5rem;
      font-weight: 700;
    }
    p {
      font-family: "Playfair Display", serif;
    }
  }
`;

const ProductCard = () => {
  return (
    <CardStyled>
      <a href="">
        <div className="img-box">
          <img src={CocoFiber} alt="Coconut Fiber" />
        </div>
      </a>
      <div className="text-box">
        <a href="">
          <h3>Coconut Fiber</h3>
        </a>

        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Laudantium
          earum iste consequatur?
        </p>
      </div>
    </CardStyled>
  );
};

export default ProductCard;
