  padding: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  div.txt-box {
    h4 {
      font-size: 1.75rem;
    }
    p {
      font-size: 1rem;
    }
  }
  div.img-box {
    img {
      border-radius: 50%;
      max-width: 100%;
      height: auto;
    }
  }
  @media screen and (max-width: 768px) {
    flex-direction: column;
    div.txt-box {
    }
  }
`;

const TeamCard = () => {
  return (
    <CardStyled>
      <div className="img-box">
        <img src={avatar} alt="avatar" />
      </div>
      <div className="txt-box">
        <h4>Full Name</h4>
        <p>
          Lorem, ipsum dolor sit amet consectetur adipisicing elit. Voluptas,
          provident quisquam distinctio aspernatur ipsa quibusdam molestiae.
          Sequi rem consectetur iste.
        </p>
      </div>
    </CardStyled>
  );
};

export default TeamCard;
