import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { FaBars } from "react-icons/fa";

const NavbarStyled = styled.nav`
  background: #000;
  height: 60px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem calc((100vw - 1000px) / 2);
  z-index: 10;

  div.nav-menu {
    display: flex;
    align-items: center;

    @media screen and (max-width: 768px) {
      display: none;
    }
  }
`;

const NavlinkStyled = styled(NavLink)`
  text-decoration: none;
  color: #fff;
  display: flex;
  align-items: center;
  padding: 0 1rem;
  height: 100%;
  cursor: pointer;
  border-bottom: 1px solid rgba(0, 0, 0, 0);

  &.active {
    color: #15cdfc;
  }
  &.navbar-brand {
    font-size: 1.5rem;
  }
`;
const Bars = styled(FaBars)`
  display: none;
  color: #fff;
  @media screen and (max-width: 768px) {
    display: block;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 75%);
    font-size: 1.8rem;
    cursor: pointer;
  }
`;

const Navbar = () => {
  return (
    <NavbarStyled>
      <NavlinkStyled to="/" className="navbar-brand">
        Universal Coco
      </NavlinkStyled>
      <Bars />
      <div className="nav-menu">
        <NavlinkStyled to="/about" activeStyle>
          About
        </NavlinkStyled>
        <NavlinkStyled to="/contact" activeStyle>
          Contact Us
        </NavlinkStyled>
      </div>
    </NavbarStyled>
  );
};

export default Navbar;
