import React from "react";
import styled from "styled-components";
import { FaLinkedin, FaInstagramSquare } from "react-icons/fa";

const CardStyled = styled.div`
  padding: 2rem;
  display: flex;
  flex-direction: column;

  align-items: center;

  div.txt-box {
    font-family: "Playfair Display", serif;
    text-align: center;
    h4 {
      font-size: 1.5rem;
    }
    p.role {
      font-weight: bold;
      color: #333;
    }
    p {
      font-size: 1rem;
      color: rgb(130, 130, 130);
    }
    div.team-social {
      padding: 1rem 0 0;
      a {
        padding: 0 0.5rem;
        color: inherit;
        text-decoration: none;
      }
    }
  }
  div.img-box {
    img {
      transform: scale(0.8);

      max-width: 100%;
      height: auto;
    }
  }
  @media screen and (max-width: 768px) {
    div.txt-box {
      h4 {
        font-size: 1.3rem;
      }

      p {
        font-size: 0.8rem;
      }
    }
  }
`;

const TeamCard = ({ avatar, name, role, quote, linkedin, instagram }) => {
  return (
    <CardStyled>
      <div className="img-box">
        <img src={avatar} alt="avatar" />
      </div>
      <div className="txt-box">
        <h4>{name}</h4>
        <p className="role">{role}</p>
        <p>{quote}</p>
        <div className="team-social">
          <a href={linkedin}>
            <FaLinkedin />
          </a>
          <a href={instagram}>
            <FaInstagramSquare />
          </a>
        </div>
      </div>
    </CardStyled>
  );
};

export default TeamCard;
