import React from "react";
import styled from "styled-components";
import { GalleryImg } from "../../Data";
import cocoplantation from "../../images/coconutplantation.jpeg";
const GalleryStyled = styled.div`
  padding: 1rem;
  gap: 1rem;
  background-image: url(${cocoplantation});
  background-size: cover;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));

  img {
    width: 100%;
    height: auto;
    max-height: 100%;
  }
`;

const Gallery = () => {
  return (
    <GalleryStyled>
      {GalleryImg.map((img, idx) => {
        return <img src={img} key={idx} alt={`img${idx}`} />;
      })}
    </GalleryStyled>
  );
};

export default Gallery;
