import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import { FaBars } from "react-icons/fa";
import { MdClose } from "react-icons/md";

const NavbarStyled = styled.nav`
  position: relative;

  height: 60px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem calc((100vw - 1000px) / 2);
  z-index: 10;
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  text-align: center;

  @media screen and (max-width: 768px) {
    transition: 0.5s ease-in-out;
    position: absolute;
    flex-direction: column;
    justify-content: center;
    gap: 20px;
    align-items: center;
    top: ${({ isOpen }) => (isOpen ? "0" : "-1500%")};
    opacity: ${({ isOpen }) => (isOpen ? "100%" : "0")};
    right: 0;
    width: 100%;
    height: 100vh;
    background-color: rgba(15, 15, 15, 0.6);
    z-index: 20;
  }
`;

const NavlinkStyled = styled(NavLink)`
  color: #000;
  text-decoration: none;
  display: flex;
  align-items: center;
  padding: 0 1rem 0.3rem;
  cursor: pointer;
  border-bottom: 2px solid rgba(0, 0, 0, 0);
  &:hover:not(.navbar-brand) {
    border-color: #c2c07d;
  }
  &.active:not(.navbar-brand) {
    border-color: #c2c07d;
  }
  &.navbar-brand {
    font-size: 1.5rem;
    color: #000;
  }
  @media screen and (max-width: 768px) {
    color: #fff;
  }
`;
const MobileMenu = styled.div`
  display: none;

  @media screen and (max-width: 768px) {
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    right: 0;
    transform: translate(-100%, 75%);
    font-size: 1.8rem;
    cursor: pointer;
    z-index: 21;
  }
`;

const Navbar = () => {
  const [burgerOpen, setBurgerOpen] = useState(false);
  return (
    <NavbarStyled>
      <NavlinkStyled to="/" className="navbar-brand">
        Universal Coco
      </NavlinkStyled>
      <MobileMenu onClick={() => setBurgerOpen(!burgerOpen)}>
        {burgerOpen ? <MdClose color="#fff" /> : <FaBars color="#000" />}
      </MobileMenu>
      <NavMenu isOpen={burgerOpen}>
        <NavlinkStyled to="/about">About</NavlinkStyled>
        <NavlinkStyled to="/contact">Contact Us</NavlinkStyled>
      </NavMenu>
    </NavbarStyled>
  );
};

export default Navbar;
