import React from "react";
import styled from "styled-components";
import logo from "../images/logo-uai.webp";
import { MdEmail, MdLocationPin } from "react-icons/md";
import { AiFillPhone } from "react-icons/ai";

const FooterStyled = styled.div`
  background-color: #3b3d3f;
  color: #fff;
  div.footer-container {
    display: flex;
    padding: 3rem;

    div.left-part {
      flex: 1;
      img {
        width: 40%;
      }
      h1 {
        font-family: "Inter", sans-serif;
        padding: 1rem 0;
      }
    }
    div.right-part {
      flex: 1;
      display: flex;
      flex-direction: column;
      justify-content: space-around;
      p {
        font-weight: 300;
        font-size: 1.5rem;
      }
    }
  }
  div.copyright {
    background-color: black;
    font-size: 0.8rem;
    padding: 0.5rem 0;
    text-align: center;
  }

  @media (max-width: 768px) {
    div.footer-container {
      padding: 1rem 2rem;
      flex-direction: column;
      align-items: center;
      div.left-part {
        img {
          align-self: center;
        }
        h1 {
          font-size: 1.3rem;
        }
      }
      div.right-part {
        p {
          font-size: 1rem;
        }
      }
    }
  }
`;

const Footer = () => {
  return (
    <FooterStyled>
      <div className="footer-container">
        <div className="left-part">
          <img src={logo} alt="UAI logo" />
          <h1>PT Universal Agrobisnis Indonesia</h1>
        </div>
        <div className="right-part">
          <p>
            <AiFillPhone /> +62 811 9444 283
          </p>
          <p>
            <MdEmail /> universalagrobisnis@gmail.com
          </p>
          <p>
            <MdLocationPin />
            Jl. Raya Abdullah Safe'i No.1 / Tebet Utara Dalam No.34, RT.5/RW.1,
            Tebet Tim., Tebet, Kota Jakarta Selatan, Daerah Khusus Ibukota
            Jakarta 12820
          </p>
        </div>
      </div>
      <div className="copyright">
        <p>Copyright Universal Agrobisnis Indonesia 2021</p>
      </div>
    </FooterStyled>
  );
};

export default Footer;
