import React from "react";
import styled from "styled-components";
import img from "../../images/about-hero.webp";

const HeroStyled = styled.div`
  width: 100%;
  padding: 2rem 3rem;
  display: flex;
  gap: -20px;
  align-items: center;
  div.img {
    width: 100%;

    left: 0;
    overflow: visible;
    img {
      width: 100%;
      height: 100%;
      height: auto;
    }
  }
  div.hero-text {
    padding: 2rem;

    background: linear-gradient(
      91.1deg,
      rgba(244, 246, 245, 0.9) -7.89%,
      rgba(255, 255, 255, 0.9) 132.55%
    );
  }
`;
const HeroSection = () => {
  return (
    <HeroStyled>
      <div className="img">
        <img src={img} alt="" />
      </div>
      <div className="hero-text">
        <h1>PT Universal Agrobisnis Indonesia</h1>
        <p>
          PT Universal Agrobisnis Indonesia is an Indonesian company engaged in
          the export of coconut derivative products,supplying processed coconut
          products. We provide the best and high quality coconut products from
          Indonesia for the whole world.
        </p>
      </div>
    </HeroStyled>
  );
};

export default HeroSection;
